function cameraStart(){navigator.mediaDevices.getUserMedia(constraints).then(function(e){track=e.getTracks()[0],cameraView.srcObject=e}).catch(function(e){console.error("Oops. Something is broken.",e)})}var constraints={video:{facingMode:"user"},audio:!1},track=null;const cameraView=document.querySelector("#camera--view"),cameraOutput=document.querySelector("#camera--output"),cameraSensor=document.querySelector("#camera--sensor"),cameraTrigger=document.querySelector("#camera--trigger");cameraTrigger.onclick=function(){cameraSensor.width=cameraView.videoWidth,cameraSensor.height=cameraView.videoHeight,cameraSensor.getContext("2d").drawImage(cameraView,0,0),cameraOutput.src=cameraSensor.toDataURL("image/webp"),cameraOutput.classList.add("taken")},window.addEventListener("load",cameraStart,!1);